@AccessControl.authorizationCheck: #NOT_REQUIRED
@EndUserText.label: 'Online Pos Başlık Verileri'
@Metadata.ignorePropagatedAnnotations: true
define root view entity YOP_DDL_I_POSHEADER
  as select distinct from YOP_DDL_I_POSDATA as PosHeader
{
  key Bukrs,
  key BankNo,
  key BankName,
  key WorkplaceNo,
      //  key TerminalNo,
  key TransactionDate, 
  key ValueDate,
  key ProcessType,
      //  key FileName,
      //  key CreateDate,
      //  key CreateTime,
      //  key CreateUser,
      //  key AccBelnr,
      //  key AccCreateDate,
      //  key AccCreateTime,
      //  key AccCreateUser,
  key CurrencyCode,
      WorkplaceDesc,
      AccDocument,
      OrgRefDoc,
      RevDoc,
      gjahr,
      @Semantics.amount.currencyCode : 'CurrencyCode'
      sum( GrossAmount )      as GrossAmount,
      @Semantics.amount.currencyCode : 'CurrencyCode'
      sum( NetAmount )        as NetAmount,
      @Semantics.amount.currencyCode : 'CurrencyCode'
      sum( CommissionAmount ) as CommissionAmount,
      @Semantics.amount.currencyCode : 'CurrencyCode'
      sum( InterruptAmount )  as InterruptAmount,
      //cast(
      //  case
      //    when cast( sum( NetAmount ) as abap.dec( 23, 2 ) ) = 0
      //    then 0
      //    else ( cast( sum( CommissionAmount ) as abap.dec( 23, 2 ) )
      //           / cast( sum( GrossAmount ) as abap.dec( 23, 2 ) ) )
      //         * cast( 100 as abap.dec( 23, 2 ) )
      //  end
      //  as abap.dec( 23, 2 )
      //) as CommissionRate
      //

      cast(
        case
          when cast( sum( GrossAmount ) as abap.dec( 23, 2 ) ) = 0
          then 0
          else ( cast( sum( CommissionAmount ) as abap.dec( 23, 2 ) )
                 / cast( sum( GrossAmount ) as abap.dec( 23, 2 ) ) )
               * cast( 100 as abap.dec( 23, 2 ) )
        end
        as abap.dec( 23, 2 )
      )                       as CommissionRate
}
group by
  Bukrs,
  BankNo,
  BankName,
  WorkplaceNo,
  //  TerminalNo,
  TransactionDate,
  ValueDate,
  ProcessType,
  WorkplaceDesc,
  AccDocument,
  OrgRefDoc,
  RevDoc,
  gjahr,
  //  FileName,
  //  CreateDate,
  //  CreateTime,
  //  CreateUser,
  //  AccBelnr,
  //  AccCreateDate,
  //  AccCreateTime,
  //  AccCreateUser,
  CurrencyCode
