define view entity ZTAX_DDL_I_BRF_NODE
  as select from ztax_t_mg
  association        to parent ZTAX_DDL_I_BRF_SUBHEAD as _SubHead      on  $projection.CompanyCode = _SubHead.CompanyCode
                                                                       and $projection.Refrection1 = _SubHead.Refrection1
                                                                       and $projection.Refrection2 = _SubHead.Refrection2

  association [1..1] to ZTAX_DDL_I_BRF_COMPANY_CODE   as _CompanyCodes on  $projection.CompanyCode = _CompanyCodes.CompanyCode
 

{
  key bukrs  as CompanyCode,
  key kiril1 as Refrection1,
  key kiril2 as Refrection2,
  key hkont  as GLAccount,
  key mindk  as MinorityIndicator,
      _SubHead ,
      _CompanyCodes 
}
